/* * * File  : AESEncryptor.java * Project:     BBB */package com.bbb.framework.crypto;/** * A simple class for performing encryption/decryption operations using the AES cipher. *  * */public class AESEncryptor extends AbstractEncryptor {    /**     * Generated serial version UID.     */    private static final long serialVersionUID = 536714502387566490L;    /**     * AES encryption algorithm.     */    private static final String ALGORITHM = "AES";    /**     * @param pKey     *                the byte[] key which should be used to instantiate the encryptor.     * @throws EncryptorException     *                 if the encryptor cannot be instantiated using the key and algorithm provided.     */    public AESEncryptor(final byte[] pKey) throws EncryptorException {    	super(pKey);    }    /**     * @param pKey     *                the String key which should be used to instantiate the encryptor.     * @throws EncryptorException     *                 if the encryptor cannot be instantiated using the key and algorithm provided.     */    public AESEncryptor(final String pKey) throws EncryptorException {    	super(pKey);    }        public AESEncryptor() throws EncryptorException {    	super("Dz95mH1tOySrMlGLhUaICQ==");    }    /**     * Returns the name of the algorithm this encryptor should use.     *      * @return "AES"     */    protected String getAlgorithmName() {    	return ALGORITHM;    }        }